def update_gitignore(root=".", v=False):
    """Add the following entries to the `.gitignore` file:
    ```
        _autosave*
        *bak
        *.xml
        .ipynb_checkpoints
        *-erc.txt
        *-drc.txt
        kibot_errors.filter
    ```
    """
    # Add filters for kicad project and schematic files
    _add_lines_to_file(root,
                       ".gitignore",
                       [line + "\n" for line in _gitignore_list],
                       v
    )

